function checkButtonWorkspacePartLocalization() {
    function callback() {
        let popup_contents_box = document.getElementsByClassName("popup_contents_box")[0];

        //제목
        let title = popup_contents_box.getElementsByClassName("create_work_input")[0];
        title = title.getElementsByTagName("input")[0].value.trim();
        //클라우드 파일 선택
        let cloudFileValue = null;
        if (popup_contents_box.getElementsByClassName("upload_work_part_clound_files_wrap").length != 0) {
            let cloudFile = popup_contents_box.getElementsByClassName("upload_work_part_clound_files_wrap")[0];
            cloudFileValue = cloudFile.getAttribute("value");
        }
    
        let button = popup_contents_box.getElementsByClassName("create_work_step2_bottom_button")[0];
        if (title != "" && cloudFileValue != 0) {
            button.classList.remove("create_work_step2_bottom_button_disabled");
        } else {
            button.classList.add("create_work_step2_bottom_button_disabled");
        }
    }
    window.requestAnimationFrame(callback);
}
function checkEditButtonWorkspacePartLocalization() {
    function callback() {
        let popup_contents_box = document.getElementsByClassName("popup_contents_box")[0];
        let button = popup_contents_box.getElementsByClassName("create_work_step2_bottom_button")[0];

        if (button.getAttribute("type") == "edit") {
            //작품 제목
            let title = popup_contents_box.getElementsByClassName("create_work_input")[0];
            title = title.getElementsByTagName("input")[0].value.trim();
        
            if (title != "" && button.getAttribute("original_data") != JSON.stringify(getInfoWorkspacePartLocalization())) {
                button.classList.remove("create_work_step2_bottom_button_disabled");
            } else {
                button.classList.add("create_work_step2_bottom_button_disabled");
            }
        }
    }
    window.requestAnimationFrame(callback);
}









function getInfoWorkspacePartLocalization() {
    let popup_contents_box = document.getElementsByClassName("popup_contents_box")[0];

    //작품 표지
    let thumbnailImage = popup_contents_box.getElementsByClassName("workspace_work_cover")[0];
    let thumbnailImageUrl = null;
    if (thumbnailImage.getAttribute("value") == "1" || thumbnailImage.getAttribute("value") == 1) {
        thumbnailImageUrl = thumbnailImage.getElementsByTagName("img")[0].src;
    }

    //회차 제목
    let title = popup_contents_box.getElementsByClassName("create_work_input")[0];
    title = title.getElementsByTagName("input")[0].value.trim();

    //언어
    let edit_language = document.getElementById("workspace_part_localization_edit_language");
    if (edit_language == null) {
        language = popup_contents_box.getElementsByClassName("create_work_step2_option_box")[0].getAttribute("value");
    } else {
        language = edit_language.innerHTML.trim();
    }

    //
    let editType = document.getElementById("workspace_part_localization_type");
    editType = editType.innerHTML.trim();

    //클라우드 파일 번호
    let fileNumber = null;
    if (popup_contents_box.getElementsByClassName("upload_work_part_clound_files_wrap").length != 0) {
        let clound_files_wrap = popup_contents_box.getElementsByClassName("upload_work_part_clound_files_wrap")[0];
        if (clound_files_wrap.getAttribute("value") == "1" || clound_files_wrap.getAttribute("value") == 1) {
            fileNumber = clound_files_wrap.getAttribute("fileNumber");
        }
    }

    let data = {
        'type': editType,
        'language': language,
        'thumbnailImage': thumbnailImageUrl,
        'title': title
    }
    if (fileNumber != null) {
        data["fileNumber"] = fileNumber;
    }

    return data;
}








