function requestWorkspacePartLocalizationThumbnailUpload(file) {
    spinLoading();

    const xhr = new XMLHttpRequest();
    const method = "POST";
    
    xhr.open(method, "https://img.louibooks.com/upload.php");
    
    xhr.addEventListener('readystatechange', function (event) {
        const { target } = event;
        if (target.readyState === XMLHttpRequest.DONE) {
            const { status } = target;
            if (status === 0 || (status >= 200 && status < 400)) {
                let xhrHtml = xhr.responseText;
                let json = JSON.parse(xhrHtml);

                let popup_contents_box = document.getElementsByClassName("popup_contents_box")[0];
                let thumbnail = popup_contents_box.getElementsByClassName("workspace_work_cover")[0];
                thumbnail.setAttribute("value", "1");
                thumbnail.getElementsByTagName("img")[0].setAttribute("style", null);
                thumbnail.getElementsByTagName("img")[0].src = json["url"];
                
                actionMessage(getLanguage("upload_image"));
                checkEditButtonWorkspacePartLocalization();
            } else {
                if (status == 504) {
                    //시간 초과
                    serverResponseErrorMessage(0);
                } else {
                    //오류 발생
                    serverResponseErrorMessage(1);
                }
            }
            spinLoadingComplete();
        }
    });
    
    var formData = new FormData();
    formData.append("imgFile", file);
    formData.append("type", "work_part_thumbnail");

    xhr.send(formData);
}




























