function requestEditWorkspacePartLocalization(info) {
    let menuNumber = getCurrentMenuNumber();
    let contents = document.getElementById("contents_" + menuNumber);

    spinLoading();

    const xhr = new XMLHttpRequest();
    const method = "POST";
    
    xhr.open(method, "/menu/workspace/php/part/localization/edit.php");
    
    xhr.addEventListener('readystatechange', function (event) {
        const { target } = event;
        if (target.readyState === XMLHttpRequest.DONE) {
            const { status } = target;
            if (status === 0 || (status >= 200 && status < 400)) {
                let xhrHtml = xhr.responseText.trim();
                
                if (xhrHtml == "success") {
                    actionMessage(getLanguage("workspace_localization_edit_language_message"));
                    history.back();

                    //내용 변경
                    let item = null;
                    let items = contents.getElementsByClassName("workspace_part_contents_localization_item");
                    for (let i = 0; i < items.length; i++) {
                        if (items[i].getAttribute("language") == info["language"]) {
                            item = items[i];
                        }
                    }
                    item.getElementsByClassName("workspace_part_contents_localization_item_data")[0].innerHTML = JSON.stringify({
                        "edit": true,
                        "type": info["type"],
                        "partNumber": info["partNumber"],
                        "language": info["language"],
                        "thumbnailImage": info["thumbnailImage"],
                        "title": info["title"]
                    });

                    //제목
                    let itemTitle = item.getElementsByClassName("workspace_part_contents_localization_item_center_description")[0];
                    itemTitle.innerHTML = info["title"];
                }
            } else {
                if (status == 504) {
                    //시간 초과
                    serverResponseErrorMessage(0);
                } else {
                    //오류 발생
                    serverResponseErrorMessage(1);
                }
            }
            spinLoadingComplete();
        }
    });
    
    var formData = new FormData();
    formData.append("partNumber", info["partNumber"]);
    formData.append("language", info["language"]);
    formData.append("title", info["title"]);
    if (info["fileNumber"] != null) {
        formData.append("fileNumber", info["fileNumber"]);
    }
    if (info["thumbnailImage"] != null) {
        formData.append("thumbnailImage", info["thumbnailImage"]);
    }

    xhr.send(formData);
}






































function getWorkspacePartLocalizationCreateLanguageSelectList(alreadyCreatedLanguage) {
    alreadyCreatedLanguage = alreadyCreatedLanguage.split(",");

    let items = getLanguageSelectItem();
    let newItems = new Array();
    for (let i = 0; i < items.length; i++) {
        if (alreadyCreatedLanguage.includes(items[i]["value"]) == false) {
            newItems[newItems.length] = items[i];
        }
    }
    return newItems;
}